mapper.select:
select * from Article

            articleId = #{searchPamater.articleId}
            articleId BETWEEN
                #{searchPamater.articleIdMin} to #{searchPamater.articleIdMax}

            AND title like "%"#{searchPamater.title}"%"
            AND subTitle like "%"#{searchPamater.subTitle}"%"
            AND content like "%"#{searchPamater.content}"%"
            AND seo like "%"#{searchPamater.seo}"%"
            AND createTime = #{searchPamater.createTime}
            AND createTime
                BETWEEN #{searchPamater.createTimeMin} to #{searchPamater.createTimeMax}

            AND updateTime = #{searchPamater.updateTime}
            AND updateTime
                BETWEEN #{searchPamater.updateTimeMin} to #{searchPamater.updateTimeMax}
