mapper.select:
select * from Custom

            customId = #{searchPamater.customId}
            customId BETWEEN
                #{searchPamater.customIdMin} to #{searchPamater.customIdMax}

            AND customPersonId = #{searchPamater.customPersonId}
            AND
                customPersonId BETWEEN #{searchPamater.customPersonIdMin} to #{searchPamater.customPersonIdMax}

            AND nickName like "%"#{searchPamater.nickName}"%"
            AND photo like "%"#{searchPamater.photo}"%"
            AND group = #{searchPamater.group}
            AND group BETWEEN
                #{searchPamater.groupMin} to #{searchPamater.groupMax}

            AND createTime = #{searchPamater.createTime}
            AND createTime
                BETWEEN #{searchPamater.createTimeMin} to #{searchPamater.createTimeMax}

            AND updateTime = #{searchPamater.updateTime}
            AND updateTime
                BETWEEN #{searchPamater.updateTimeMin} to #{searchPamater.updateTimeMax}
