mapper.select:
select * from CustomPerson

            customPersonId = #{searchPamater.customPersonId}

                customPersonId BETWEEN #{searchPamater.customPersonIdMin} to #{searchPamater.customPersonIdMax}

            AND name like "%"#{searchPamater.name}"%"
            AND sex = #{searchPamater.sex}
            AND sex BETWEEN
                #{searchPamater.sexMin} to #{searchPamater.sexMax}

            AND birthday = #{searchPamater.birthday}
            AND birthday BETWEEN
                #{searchPamater.birthdayMin} to #{searchPamater.birthdayMax}

            AND carID like "%"#{searchPamater.carID}"%"
            AND createTime = #{searchPamater.createTime}
            AND createTime
                BETWEEN #{searchPamater.createTimeMin} to #{searchPamater.createTimeMax}

            AND updateTime = #{searchPamater.updateTime}
            AND updateTime
                BETWEEN #{searchPamater.updateTimeMin} to #{searchPamater.updateTimeMax}
