mapper.select:
select * from Order

            orderId = #{searchPamater.orderId}
            orderId BETWEEN
                #{searchPamater.orderIdMin} to #{searchPamater.orderIdMax}

            AND orderCode like "%"#{searchPamater.orderCode}"%"
            AND spuId = #{searchPamater.spuId}
            AND spuId BETWEEN
                #{searchPamater.spuIdMin} to #{searchPamater.spuIdMax}

            AND skuId = #{searchPamater.skuId}
            AND skuId BETWEEN
                #{searchPamater.skuIdMin} to #{searchPamater.skuIdMax}

            AND price = #{searchPamater.price}
            AND price BETWEEN
                #{searchPamater.priceMin} to #{searchPamater.priceMax}

            AND status = #{searchPamater.status}
            AND status BETWEEN
                #{searchPamater.statusMin} to #{searchPamater.statusMax}

            AND createTime = #{searchPamater.createTime}
            AND createTime
                BETWEEN #{searchPamater.createTimeMin} to #{searchPamater.createTimeMax}

            AND updateTime = #{searchPamater.updateTime}
            AND updateTime
                BETWEEN #{searchPamater.updateTimeMin} to #{searchPamater.updateTimeMax}
