mapper.select:
select * from
        Rel_WebSite_Channel_Article

            Id = #{searchPamater.Id}
            Id BETWEEN #{searchPamater.IdMin} to
                #{searchPamater.IdMax}

            AND webSiteId = #{searchPamater.webSiteId}
            AND webSiteId BETWEEN
                #{searchPamater.webSiteIdMin} to #{searchPamater.webSiteIdMax}

            AND channelId = #{searchPamater.channelId}
            AND channelId BETWEEN
                #{searchPamater.channelIdMin} to #{searchPamater.channelIdMax}

            AND articleId = #{searchPamater.articleId}
            AND articleId BETWEEN
                #{searchPamater.articleIdMin} to #{searchPamater.articleIdMax}

            AND createTime = #{searchPamater.createTime}
            AND createTime
                BETWEEN #{searchPamater.createTimeMin} to #{searchPamater.createTimeMax}
